CREATE TABLE Bus (Bus_No varchar(50) NOT NULL, Author varchar(50) NOT NULL, Total_capacity int(2) NOT NULL, Last_lat double, Last_lon double, seat_plan varchar(50) NOT NULL, ac_status tinyint(1) NOT NULL, PRIMARY KEY (Bus_No));
CREATE TABLE daily_schedule (ID varchar(50) NOT NULL, company_name varchar(50) NOT NULL, Source_Location varchar(255) NOT NULL, Destination_location varchar(255) NOT NULL, Total_capacity int(2) NOT NULL, Dep_time_hour int(2) NOT NULL, Dep_time_minute int(2) NOT NULL, Fare double, bus_no varchar(50), PRIMARY KEY (ID));
CREATE TABLE schedule_record (schedule_id varchar(100) NOT NULL, date_time datetime NOT NULL, bus_no varchar(50) NOT NULL, free_seat_nmbr_int int(2) NOT NULL, free_seat_no_details_str varchar(50), PRIMARY KEY (schedule_id));
CREATE TABLE counter (ID varchar(50) NOT NULL, Name varchar(50) NOT NULL, Contact_number varchar(15) NOT NULL, Location varchar(255) NOT NULL, Lat double NOT NULL, Lon double NOT NULL, company_name varchar(50) NOT NULL, PRIMARY KEY (ID));
CREATE TABLE `user` (Phone_no varchar(15) NOT NULL, Name varchar(30) NOT NULL, password varchar(200) NOT NULL, PRIMARY KEY (Phone_no));
CREATE TABLE emergency_contact_numbers (User_phone_no varchar(15) NOT NULL, no_1 varchar(50), no_2 varchar(50), no_3 varchar(50), PRIMARY KEY (User_phone_no));
CREATE TABLE hospital (lat double NOT NULL, lon double NOT NULL, Hospital_name varchar(50) NOT NULL, Contact_no varchar(15) NOT NULL, address varchar(500) NOT NULL, PRIMARY KEY (lat, lon));
CREATE TABLE bus_location_record (schedule_id varchar(100) NOT NULL, date_time datetime NOT NULL, lat double NOT NULL, lon double NOT NULL, PRIMARY KEY (schedule_id));
CREATE TABLE police (lat double NOT NULL, lon double NOT NULL, name varchar(50) NOT NULL, contact_no varchar(15) NOT NULL, address varchar(500) NOT NULL, PRIMARY KEY (lat, lon));
CREATE TABLE ticket (ticket_id varchar(100) NOT NULL, schedule_id varchar(100) NOT NULL, user_phn_no varchar(15) NOT NULL, seat_no varchar(10) NOT NULL, issed_date_time datetime NOT NULL, fare double NOT NULL, issued_by varchar(30) NOT NULL, bus_no varchar(50) NOT NULL, PRIMARY KEY (ticket_id));
CREATE TABLE accident_record (schedule_record_id varchar(100) NOT NULL, bus_no varchar(50) NOT NULL, date_time datetime NOT NULL, alert_hospital_lat double, alert_hospital_lon double, alert_police_lat double, alert_police_lon double, counter_id varchar(50), users_emergency_nmbrs varchar(2500), PRIMARY KEY (schedule_record_id));
CREATE TABLE bus_ticket (schedule_id int(11), `Column` int(10));
CREATE TABLE authority (phn_no varchar(15) NOT NULL, name varchar(50) NOT NULL, counter_id varchar(50) NOT NULL, password varchar(200) NOT NULL, PRIMARY KEY (phn_no));
CREATE TABLE cityName_to_code (city_type varchar(50) NOT NULL, city_name varchar(50) NOT NULL, city_code varchar(10) NOT NULL);
CREATE TABLE companyName_to_code (company_name varchar(50) NOT NULL, company_code varchar(10) NOT NULL);
CREATE TABLE schedule (id varchar(100) NOT NULL, date_time datetime NOT NULL, daily_schedule_id varchar(50) NOT NULL, bus_no varchar(50) NOT NULL, available_capacity int(2) NOT NULL, total_capacity int(2) NOT NULL, fare double NOT NULL, PRIMARY KEY (id));
CREATE TABLE seat_condition (schedule_id int(11) NOT NULL, 1A tinyint(1) NOT NULL, 1B tinyint(1) NOT NULL, 1C tinyint(1) NOT NULL, 1D tinyint(1) NOT NULL, 2A tinyint(1) NOT NULL, 2B tinyint(1) NOT NULL, 2C tinyint(1) NOT NULL, 2D tinyint(1) NOT NULL, 3A tinyint(1) NOT NULL, 3B tinyint(1) NOT NULL, 3C tinyint(1) NOT NULL, 3D tinyint(1) NOT NULL, 4A tinyint(1) NOT NULL, 4B tinyint(1) NOT NULL, 4C tinyint(1) NOT NULL, 4D tinyint(1) NOT NULL, 5A tinyint(1) NOT NULL, 5B tinyint(1) NOT NULL, 5C tinyint(1) NOT NULL, 5D tinyint(1) NOT NULL, 6A tinyint(1) NOT NULL, 6B tinyint(1) NOT NULL, 6C tinyint(1) NOT NULL, 6D tinyint(1) NOT NULL, 7A tinyint(1) NOT NULL, 7B tinyint(1) NOT NULL, 7C tinyint(1) NOT NULL, 7D tinyint(1) NOT NULL, 8A tinyint(1) NOT NULL, 8B tinyint(1) NOT NULL, 8C tinyint(1) NOT NULL, 8D tinyint(1) NOT NULL, 9A tinyint(1) NOT NULL, 9B tinyint(1) NOT NULL, 9C tinyint(1) NOT NULL, 9D tinyint(1) NOT NULL, 10A tinyint(1) NOT NULL, 10B tinyint(1) NOT NULL, 10C tinyint(1) NOT NULL, 10D tinyint(1) NOT NULL);
